package JavaBasics;

public class IncrementAndDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		int j=i++;
		//Post increment
				
		//pre increment
		int a=10;
		int b=++a;
		
		System.out.println(i);
		System.out.println(j);
		
		System.out.println(a);
		System.out.println(b);
		
	}

}
