package JavaBasics;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// '+' is used for concatenation operator 
		// println is used to print console new line, 
		// print is used to print same line
		
		// '+' is used for concatenation operator 
				// println is used to print console new line, 
				// print is used to print same line
				
		
		int a=100;
		int b=200;
		String x="shivakumar";
		String y="shamithatadaga";
		
		double d=1.233;
		double f=1.5455;
		
		System.out.print(a+b);
		System.out.print(a+b+x+y);
		System.out.print(a+x+y+b);
		System.out.print(d+f);
		System.out.print(a+b+d+f);
		
		System.out.println("hello world and string concatenation");
		
		System.out.println(a+b);
		System.out.println(a+b+x+y);
		System.out.println(a+x+y+b);
		System.out.println(d+f);
		System.out.println(a+b+d+f);
		
		System.out.println("print the value="+(d+f));
	}

}
